//MON JSON DE MUSIQUE////////////////
var playList = [
	{titre:'Black Swan',
		url:'http://stream1.addictradio.net/addictalternative.mp3',
		img:'img/pict/img1.jpeg'},
	{titre:'Claire de lune',
		url:'http://novazz.ice.infomaniak.ch/novazz-128.mp3',
		img:'img/pict/img2.jpg'},
	{titre:'Für Elise',
		url:'http://ouifm2.ice.infomaniak.ch/ouifm2.mp3',
		img:'img/pict/img3.jpg'}
];

//MON JS ////////////////
//On attend le chargement complet du document
window.addEventListener('load', function(){

//MES VARIABLES////////////////
	//Selection de la piste audio
	var audio       = document.getElementById('mon_son');
	var source      = audio.getAttribute('src');

	//Selection des button dans des variables
	var bPlay       = document.getElementById('butt_play');
    var bStop       = document.getElementById('butt_stop');
    var bNext       = document.getElementById('butt_next');
    var bPrevius    = document.getElementById('butt_previus');
    var bVolume     = document.getElementById('volume');

    //Selection des éléments d'information
	var titre       = document.querySelector('h2');
	var pict        = document.getElementById('pict');

    //Musique en cours
	var currentI    =0;

	//Initialisation de l'affichage du volume en %
    document.querySelector('label').innerHTML=bVolume.value*100+'%';

//MES FONCTIONS////////////////
	//Fonction Play song
    function player() {
        audio.setAttribute('src', playList[currentI].url);
        titre.innerHTML = playList[currentI].titre;
        pict.setAttribute('src', playList[currentI].img);
    }

    //Fonction Stop song
	function stopper() {
        audio.setAttribute('src', '');
        titre.innerHTML = playList[currentI].titre;
        pict.setAttribute('src', playList[currentI].img);
    }

    //Fonction nexter
	function nexter() {
        if (currentI<playList.length-1){
            currentI++;
        }
        else{
            currentI=playList.length-1;
            beep();
        }
        audio.setAttribute('src', playList[currentI].url);
        titre.innerHTML = playList[currentI].titre;
        pict.setAttribute('src', playList[currentI].img);
    }

    //Fonction préviuser
    function previuser() {
        if (currentI>0){
            currentI--;
        }
        else{
            currentI=0;
            beep();
        }
        audio.setAttribute('src', playList[currentI].url);
        titre.innerHTML = playList[currentI].titre;
        pict.setAttribute('src', playList[currentI].img);

    }

    //Fonction changer le volume
    function volumer() {
        audio.volume = bVolume.value;
        document.querySelector('label').innerHTML=bVolume.value*100+'%';
    }

    //Fonction beep sonor
    function beep() {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }

//MES ÉVENNEMENTS////////////////
    //Ajout évennement sur le clavier
    document.addEventListener('keydown', function (key) {
    		switch (key.keyCode){
    			case 32 :
    			    audio.paused==true?player():stopper();
    				break;
    			case 37 : previuser();
    				break;
    			case 39 : nexter();
    				break;
    			case 38 :
    				bVolume.stepUp();volumer();
    				if (bVolume.value == 1){
    					beep();
    				}
    				break;
    			case 40 :
    				bVolume.stepDown();volumer();
    				if (bVolume.value == 0){
    					beep();
    				}
             	break;
			}
    });

    //Ajout évennement sur button play
	bPlay.addEventListener('click', player);

    //Ajout évennement sur button stop
    bStop.addEventListener('click', stopper);

    //Ajout évennement sur button next
    bNext.addEventListener('click', nexter);

    //Ajout évennement sur button previus
    bPrevius.addEventListener('click', previuser);

	//Ajout évennement sur l'input volume
    bVolume.addEventListener('change', volumer);
});